import axios from "axios";
import faker from "../../package.json";
import { useState, useEffect } from "react";

const People = () => {
  const [kisi, setKisi] = useState([]);

  useEffect(() => {
    axios.get("https://randomuser.me/api/")
      .then((res) => (res));
  }, []);

  const postPerson = () => {
    axios.post("https://avatars.dicebear.com/api/avataaars/${id}:seed.svg`", {
      id: faker.datatype.number(),
      name: faker.name.findName(),
      phone: faker.phone.number(),
    });
  };

  return (
    <div>
      <button className="btn-lg btn-success" onClick={postPerson}>
        Ferhat
      </button>
      <div className="row">
        {kisi.map((i) => {
          const { id, name, phone } = i;

          return (
            <div className="col-12 col-sm-6 col-md-4 col-lg-2" key={id}>
              <img
                src={`https://avatars.dicebear.com/api/avataaars/${id}:seed.svg`}
                alt=""
              />
              <h5 className="text-danger mt-4">{name}</h5>
            </div>
          );
        })}
      </div>
    </div>
  );
};
export default People;
