import axios from "axios";
import { useState, useEffect } from "react";

const People = () => {
  const [kisi, setKisi] = useState([]);

  useEffect(() => {
    getUser();
  }, []);

  const getUser = () => {
    axios
      .get("https://randomuser.me/api/")
      .then((res) => setKisi(res.data.results[0]));
  };
  console.log(kisi);
  return (
    <div>
      <button className="btn-lg btn-success" onClick={getUser}>
        Ferhat
      </button>
      <div>
        <div>
          <img src={kisi.picture?.large} alt="" />
          <h3>
            {kisi.name?.title} {kisi.name?.first} {kisi.name?.last}
          </h3>
        </div>
        <div>
          <h3>{kisi.email}</h3>
        </div>
        <div>
          <h3>{kisi.phone}</h3>
        </div>
        <div>
          <h3>{kisi.location?.state} - {kisi.lo }</h3>
        </div>
      </div>
    </div>
  );
};
export default People;
