  useEffect(() => {
    axios
      .get("https://jsonplaceholder.typicode.com/users")
      .then((res) => console.log(res));
  },[]);
};

const postPerson = () => {
  axios.post("https://jsonplaceholder.typicode.com/users", {
    id: faker.datatype.number(),
    name: faker.name.findName(),
    phone:faker.phone.number(),
  });
}

